from ibm_watson import ToneAnalyzerV3from ibm_cloud_sdk_core.authenticators import IAMAuthenticatorfrom extractTweet import *import pandasimport jsonfrom json_excel_converter import Converter from json_excel_converter.xlsx import Writerapikey = 'pmR1ZaNUMctdBXlpigsX8UHBRJGJ1w4TeNKXS0KK_NC1'url = 'https://api.eu-gb.tone-analyzer.watson.cloud.ibm.com/instances/c4d4064d-fc34-4936-bed1-2f12954d1416'authenticator = IAMAuthenticator(apikey)toneAnalyser = ToneAnalyzerV3(version='2017-09-21', authenticator=authenticator)toneAnalyser.set_service_url(url)def tweetEmotions(location):    tweet = getTweets(location)    outfile = open("sample.json",'w')    writer = pd.ExcelWriter('score.xlsx', engine='xlsxwriter')    for ind in tweet.index:         response = toneAnalyser.tone(str((tweet['text'][ind]).encode("utf-8")),content_type='text/plain')        json_object = json.dumps(response.result,sort_keys=True, indent=4)        response.result["document_tone"]["tones"];        df = pd.DataFrame(response.result["document_tone"]["tones"])        print(df)        df.to_excel(writer, sheet_name='Sheet1', index=False)        outfile.write(json_object)    writer.save()                